\(&quot;, fill=&quot;right&quot;, extra=&quot;merge&quot;) %&gt;% separate(col=yr, into=c(&quot;yr&quot;,&quot;mr&quot;), sep=4) %&gt;% select(-mr, -nr) unis &lt;- bind_rows(u1,u2,u3,u4,u5,u5a,u6,u7,u7a) rm(u1,u2,u3,u4,u5,u5a,u6,u7,u7a,html,tbls) unis &lt;- unis %&gt;% mutate(yr = as.numeric(yr)) %&gt;% filter(!is.na(yr)) %&gt;% group_by(whr) %&gt;% summarise(yr = min(yr)) # wikipedia&#39;s country names not standard, so I am adding the ISO codes unis$isocode &lt;- countrycode(unis$whr, origin=&quot;country.name&quot;, destination=&quot;iso3c&quot;) # Doing business spreadsheet # the wp1 wp2 paste0 is just to fit code with margins wp1 &lt;- &quot;http://www.doingbusiness.org/data/~/media/WBG/DoingBusiness/Documents/&quot; wp2 &lt;- &quot;Miscellaneous/DB17-Distance-to-Frontier-historical-dataset.xlsx&quot; download.file(paste0(wp1, wp2), destfile=&quot;raw_data/DTF.xlsx&quot;) dtf &lt;- read_excel(&quot;raw_data/DTF.xlsx&quot;, sheet=&quot;DB05-17 for Excel&quot;) %&gt;% select(&quot;cod&quot;, &quot;economy&quot;, &quot;region&quot;, &quot;incomegroup&quot;, &quot;dbyear&quot;,&quot;DTFtradedef_db1415&quot;, &quot;tradingexportdocsdtf&quot;, &quot;tradingexporttimedtf&quot;, &quot;tradingimportdocsdtf&quot;, &quot;tradingimporttimedtf&quot;, &quot;tradingexportcost_defdtf&quot;, &quot;tradingimportcost_defdtf&quot;, &quot;tradeXcostborderdtf&quot;, &quot;tradeMcostborderdtf&quot;, &quot;tradeXtimeborderdtf&quot;, &quot;tradeMtimeborderdtf&quot;, &quot;tradeMcostdocsdtf&quot;, &quot;tradeMtimedocsdtf&quot;, &quot;tradeXcostdocsdtf&quot;, &quot;tradeXtimedocsdtf&quot;) %&gt;% mutate(dbyear = as.numeric(dbyear)) #GCI1.1 and GCI1.0 are both missing a few ISO codes gci1.1$isoCode[is.na(gci1.1$isoCode)] &lt;- countrycode(gci1.1$Country.Name[is.na(gci1.1$isoCode)], origin=&quot;country.name&quot;, destination=&quot;iso3c&quot;) gci1.0$isoCode[is.na(gci1.0$isoCode)] &lt;- countrycode(gci1.0$name[is.na(gci1.0$isoCode)], origin=&quot;country.name&quot;, destination=&quot;iso3c&quot;) ## now save everything in feather format for the other chapters write_feather(dtf, &quot;processed_data/dtf.feather&quot;) write_feather(gci1.0, &quot;processed_data/gci10.feather&quot;) write_feather(gci1.0_indicators_meta, &quot;processed_data/gci10meta.feather&quot;) write_feather(gci1.1, &quot;processed_data/gci11.feather&quot;) write_feather(gci1.1_indicators_meta, &quot;processed_data/gci11meta.feather&quot;) write_feather(GDP11, &quot;processed_data/gdp11.feather&quot;) write_feather(GDPcap11, &quot;processed_data/gdpcap11.feather&quot;) write_feather(unis, &quot;processed_data/unis.feather&quot;)
